parameters.jsCode:
const prompts = $input.first().json.output;

return prompts.map(p => ({
 json: {
 textToImagePrompt: p.textToImagePrompt.replace(/\n/g, ' ')
 }
}));

const items = $input.all();

// Extract the `url` from each item
const mediaURLs = items.map(item => item.json.url);

// Return a single item with the array named `mediaURLs`
return [
 {
 json: {
 mediaURLs: mediaURLs
 }
 }
];

