parameters.inputSchema:
{
 "name": "youtube_strategist_output",
 "description": "Generates three high-performing YouTube title and thumbnail ideas based on title trends, power words, and thumbnail analysis.",
 "type": "object",
 "properties": {
 "title_1": {
 "type": "string",
 "description": "A compelling YouTube video title idea tailored to the channel and based on provided patterns."
 },
 "thumbnail_1": {
 "type": "string",
 "description": "A short visual description of the thumbnail concept that pairs with title_1."
 },
 "title_2": {
 "type": "string",
 "description": "A second unique YouTube video title idea that fits the niche and follows successful patterns."
 },
 "thumbnail_2": {
 "type": "string",
 "description": "A short visual description of the thumbnail concept that pairs with title_2."
 },
 "title_3": {
 "type": "string",
 "description": "A third distinct YouTube video title idea optimized for the channel's audience and niche."
 },
 "thumbnail_3": {
 "type": "string",
 "description": "A short visual description of the thumbnail concept that pairs with title_3."
 }
 },
 "required": ["title_1", "thumbnail_1", "title_2", "thumbnail_2", "title_3", "thumbnail_3"]
}

