const _ = require('lodash')

const sanitize = str => _.chain(str)
 .replace(/[^A-Za-z0-9&.-]/g, '-') // sanitise via whitelist of characters
 .replace(/-(?=-)/g, '') // remove repeated dashes - https://regexr.com/6ag8h
 .trim('-') // trim any leading/trailing dashes
 .truncate({
 length: 60,
 omission: '-' // when the string ends with '-', you'll know it was truncated
 })
 .value()

const result = _.flatMap(items.map(item => {
 //console.log({item})

 // Maps each attachment to a separate item
 return _.values(item.binary).map(file => {
 console.log("Saving attachement:", file.fileName, 'from:', ...item.json.from.value)

 // sanitize filename but exclude extension
 const filename_parts = file.fileName.split('.')
 const ext = _.slice(filename_parts, filename_parts.length-1)
 const filename_main = _.join(_.dropRight(filename_parts), '.')
 file.fileName = sanitize(filename_main) + '.' + ext

 return {
 json: {
 from: sanitize(item.json.from.value[0].name),
 date: sanitize(new Date(item.json.date).toISOString().split("T")[0]) // get date part "2020-01-01"
 }, 
 binary: { file }
 }
 })
}))

//console.log(result)
return result
